sys_variable_value.value:
(function execute(inputs, outputs) {

gr = new GlideRecord("x_snc_ethereum_endpoint");

gr.addQuery("network",inputs.network);
gr.orderBy("priority");
gr.query();

while (gr.next()) {

 var r = new sn_ws.RESTMessageV2();
 r.setEndpoint(gr.uri);
 r.setHttpMethod('post');
 var payload = '{"jsonrpc":"2.0","method":"web3_clientVersion","params":[],"id":1}';
 r.setRequestBody(payload);
 r.setRequestHeader("Content-Type","application/json");

 var response = r.execute();
 var responseBody = response.getBody();
 var httpStatus = response.getStatusCode();

 gs.info("responseBody:" + responseBody);
 gs.info("httpStatus:" + httpStatus);

 if (httpStatus == "200" ) {
   outputs.endpoint = gr.sys_id;
   break;
 }

}

})(inputs, outputs);

