sys_variable_value.value:
(function execute(inputs, outputs) {
window = global;
gs.include("ethjs-account");

gr = new GlideRecord("x_snc_ethsign_key");
gr.initialize();
gr.private_key = ethAccount.generate(inputs.entropy).privateKey;
gr.address = ethAccount.generate().address;
gr.update();
})(inputs, outputs);

